import{_ as p,c as t,o as e,d as a}from"./app.b315d791.js";const f='{"title":"\u300A\u4E8C\u72D7\u5B50\u6C49\u5978\u674E\u5BCC\u8D35\u300B\u8282\u9009","description":null,"frontmatter":{"title":"\u300A\u4E8C\u72D7\u5B50\u6C49\u5978\u674E\u5BCC\u8D35\u300B\u8282\u9009","date":"2020-07-07T07:43:56.000Z","categories":["diary"],"draft":true,"tags":["\u5723\u6BCD"],"description":null},"headers":[],"relativePath":"posts/5.md"}',o={},r=a('<h1 id="\u300A\u4E8C\u72D7\u5B50\u6C49\u5978\u674E\u5BCC\u8D35\u300B\u8282\u9009" tabindex="-1">\u300A\u4E8C\u72D7\u5B50\u6C49\u5978\u674E\u5BCC\u8D35\u300B\u8282\u9009 <a class="header-anchor" href="#\u300A\u4E8C\u72D7\u5B50\u6C49\u5978\u674E\u5BCC\u8D35\u300B\u8282\u9009" aria-hidden="true">#</a></h1><blockquote><p>\u4EA8\u5229\uFF1A\u6211\u5728\u5E7F\u5DDE\u5F85\u4E86\u4E00\u9635\u5B50\uFF0C\u6211\u53D1\u89C9\u4F60\u4EEC\u4E2D\u56FD\u4EBA\u4EC0\u4E48\u90FD\u5403\u3002\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u54C8\u54C8\uFF0C\u4F60\u8BF4\u7684\u662F\u5E7F\u4E1C\u4EBA\uFF0C\u4E2D\u56FD\u522B\u7684\u5730\u65B9\u6CA1\u6709\u90A3\u4E48\u5BBD\u7684\u98DF\u8C31\uFF0C\u4E0D\u8FC7\u5E7F\u4E1C\u4EBA\u4E5F\u4E0D\u662F\u4EC0\u4E48\u90FD\u5403\uFF0C\u597D\u50CF\u4ED6\u4EEC\u5C31\u4E0D\u592A\u559C\u6B22\u5403\u72D7\u3002\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u5403\u72D7\uFF1F\u201D\u4EA8\u5229\u7684\u8BED\u8C03\u5F00\u59CB\u6253\u98A4\u4E86\u3002</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u662F\u554A\uFF0C\u5403\u72D7\uFF0C\u6709\u4EC0\u4E48\u95EE\u9898\u5417\uFF1F\u201D</p><p>\u201C\u4EBA\u600E\u4E48\u53EF\u4EE5\u5403\u72D7\u3002\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u4EBA\u4E3A\u4EC0\u4E48\u4E0D\u53EF\u4EE5\u5403\u72D7\uFF0C\u4F60\u521A\u521A\u8BF4\u4E2D\u56FD\u4EBA\u4EC0\u4E48\u90FD\u5403\uFF0C\u6211\u8FD8\u4EE5\u4E3A\u4F60\u77E5\u9053\u5462\u3002\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u6211\u521A\u624D\u8BF4\u7684\u662F\u6307\u86C7\u3001\u7334\u5B50\u4E00\u7C7B\u7684\u4E1C\u897F\uFF0C\u53EF\u662F\u72D7\u662F\u4EBA\u7C7B\u7684\u670B\u53CB\uFF0C\u4EBA\u600E\u80FD\u5403\u81EA\u5DF1\u7684\u670B\u53CB\u5462\uFF1F\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u554A\uFF0C\u5BF9\u554A\uFF0C\u72D7\u662F\u4EBA\u7C7B\u7684\u670B\u53CB\uFF0C\u6211\u600E\u4E48\u628A\u8FD9\u4E2A\u7ED9\u5FD8\u4E86\u3002\u201D\u674E\u5BCC\u8D35\u4F5C\u51FA\u4E00\u5E45\u604D\u7136\u5927\u609F\u7684\u6837\u5B50\uFF0C\u201C\u53EF\u662F\uFF0C\u4EA8\u5229\u5148\u751F\uFF0C\u4F60\u5403\u732A\u5417\uFF1F\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u5403\u554A\uFF0C\u6211\u4E0D\u662F\u7A46\u65AF\u6797\u3002\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u90A3\u96BE\u9053\u732A\u662F\u60A8\u7684\u4EC7\u4EBA\u5417\uFF1F\u201D</p><p>\u8FD9\u4E0B\u4EA8\u5229\u542C\u51FA\u674E\u5BCC\u8D35\u662F\u8BDD\u91CC\u6709\u8BDD\u4E86\uFF0C\u8FD9\u4E2A\u4E2D\u56FD\u4EBA\u4F3C\u4E4E\u60F3\u548C\u81EA\u5DF1\u8FDB\u884C\u4E00\u573A\u8FA9\u8BBA\uFF0C\u54FC\u54FC\uFF0C\u53EB\u4F60\u8FD9\u4E2A\u91CE\u86EE\u4EBA\u89C1\u8BC6\u4E00\u4E0B\u4EC0\u4E48\u662F\u54C8\u4F5B\u7684\u9AD8\u6750\u751F\uFF1A</p><p>\u4EA8\u5229\uFF1A\u201C\u732A\u5F53\u7136\u4E0D\u662F\u4EBA\u7C7B\u7684\u4EC7\u4EBA\uFF0C\u4F46\u662F\u732A\u4E5F\u4E0D\u662F\u670B\u53CB\u554A\u3002\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u4E0D\u662F\u4EC7\u4EBA\uFF0C\u4E5F\u4E0D\u662F\u670B\u53CB\uFF0C\u7B97\u4E2A\u964C\u751F\u4EBA\u5427\uFF0C\u60A8\u7684\u610F\u601D\u662F\u8BF4\u670B\u53CB\u4E0D\u80FD\u5403\uFF0C\u4F46\u662F\u53EF\u4EE5\u5403\u964C\u751F\u4EBA\uFF0C\u60A8\u53EF\u771F\u9AD8\u5C1A\u554A\uFF0C\u80FD\u6210\u4E3A\u60A8\u7684\u670B\u53CB\u6211\u975E\u5E38\u8363\u5E78\u3002\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u674E\u5C06\u519B\uFF0C\u6211\u8BF4\u670B\u53CB\u53EA\u662F\u4E00\u4E2A\u6BD4\u55BB\uFF0C\u5E76\u4E0D\u662F\u771F\u7684\u8981\u53BB\u5403\uFF0C\u8BF7\u4F60\u4E0D\u8981\u7528\u8FD9\u79CD\u5371\u8A00\u8038\u542C\u7684\u8BED\u6C14\u3002\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u597D\uFF0C\u90A3\u4E48\u6211\u4EEC\u5C31\u4E0D\u7528\u5403\u8FD9\u4E2A\u8BCD\u6765\u505A\u586B\u7A7A\uFF0C\u8FD9\u4E2A\u95EE\u9898\u7684\u4E3B\u8BED\u662F\u2018\u6211\u2019\uFF0C\u8C13\u8BED\u6709\u60A8\u6765\u586B\uFF0C\u5BBE\u8BED\u662F\u2018\u964C\u751F\u4EBA\u2019\uFF0C\u968F\u60A8\u586B\u4E0A\u4E00\u4E2A\u4EC0\u4E48\u8BCD\uFF0C\u6253\u3001\u6740\u3001\u62A2\u52AB\u3001\u5F3A\u5978\u90FD\u53EF\u4EE5\uFF0C\u7136\u540E\u6211\u4EEC\u518D\u6765\u770B\u770B\u8FD9\u53E5\u8BDD\u80FD\u4E0D\u80FD\u8BA9\u5927\u5BB6\u63A5\u53D7\u3002\u201D</p><p>\u8FD9\u4E00\u4E0B\u4EA8\u5229\u65E0\u8A00\u4EE5\u5BF9\uFF0C\u628A\u72D7\u62DF\u4EBA\u5316\u662F\u81EA\u5DF1\u63D0\u51FA\u6765\u7684\uFF0C\u73B0\u5728\u5BF9\u624B\u5C45\u7136\u7528\u8FD9\u4E2A\u529E\u6CD5\u6765\u5BF9\u4ED8\u81EA\u5DF1\uFF0C\u5982\u679C\u81EA\u5DF1\u65E0\u6CD5\u5E94\u5BF9\u7684\u8BDD\uFF0C\u8FD9\u5728\u8FA9\u8BBA\u4E2D\u5C5E\u4E8E\u5B8C\u8D25\uFF0C\u4ED6\u5B9E\u5728\u662F\u65E0\u6CD5\u63A5\u53D7\u8FD9\u6837\u4E00\u4E2A\u7ED3\u679C\u3002</p><p>\u4EA8\u5229\uFF1A\u201C\u6211\u4E0D\u8BA4\u4E3A\u732A\u53EF\u4EE5\u548C\u72D7\u76F8\u63D0\u5E76\u8BBA\uFF0C\u6211\u4EEC\u5F53\u7136\u53EF\u4EE5\u5403\u4E00\u4E9B\u6BD4\u8F83\u4F4E\u7EA7\u7684\u52A8\u7269\uFF0C\u4F46\u662F\u72D7\u4E0D\u884C\u3002\u201D\u73B0\u5728\u4ED6\u53EA\u597D\u6613\u5730\u518D\u6218\uFF0C\u4E0D\u63D0\u670B\u53CB\u90A3\u56DE\u4E8B\u4E86\u3002</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u5BF9\u5440\uFF0C\u72D7\u6BD4\u732A\u9AD8\u7EA7\uFF0C\u53EF\u662F\u8001\u9F20\u66F4\u4F4E\u7EA7\u554A\uFF0C\u4E3A\u4EC0\u4E48\u5148\u751F\u4E0D\u5403\u8001\u9F20\u5462\uFF1F\u800C\u4E14\u6211\u60F3\u8BF7\u95EE\u4E00\u4E0B\uFF0C\u4EA8\u5229\u5148\u751F\u517B\u72D7\u5417\uFF1F\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u517B\u554A\uFF0C\u6211\u5BB6\u6709\u4E24\u6761\u5FB7\u56FD\u7267\u7F8A\u72AC\uFF0C\u8FD8\u6709\u4E24\u6761\u725B\u5934\u72AC\u3002\u201D\u4ED6\u5F88\u9AD8\u5174\u53EF\u4EE5\u5229\u7528\u56DE\u7B54\u7B2C\u4E8C\u4E2A\u95EE\u9898\u6765\u56DE\u907F\u7B2C\u4E00\u4E2A\u3002</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u90A3\u4F60\u4E3A\u4ED6\u4EEC\u4EC0\u4E48\u5462\uFF1F\u732A\u8089\u5417\uFF1F\u201D</p><p>\u8FD9\u4E2A\u95EE\u9898\u663E\u7136\u6709\u9677\u9631\uFF0C\u53EF\u662F\u4EA8\u5229\u4E00\u65F6\u4E5F\u731C\u4E0D\u51FA\u5BF9\u65B9\u7684\u610F\u56FE\uFF0C\u201C\u4E5F\u5582\u732A\u8089\uFF0C\u4E5F\u5582\u725B\u8089\u3002\u201D</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u4E3A\u4EC0\u4E48\u4F60\u8BA4\u4E3A\u72D7\u6709\u6743\u5229\u5403\u732A\u5462\uFF1F\u662F\u56E0\u4E3A\u72D7\u6BD4\u732A\u9AD8\u7EA7\u5F88\u591A\u5417\uFF1F\u6BD4\u4EBA\u4E0E\u72D7\u4E4B\u95F4\u7684\u5DEE\u522B\u8FD8\u8981\u5927\uFF1F\u201D</p><p>\u53C8\u662F\u4EE5\u5B50\u4E4B\u77DB\u653B\u5B50\u4E4B\u76FE\uFF0C\u4EA8\u5229\u53D1\u73B0\u81EA\u5DF1\u7684\u5BF9\u624B\u6709\u591A\u4E48\u5F3A\u5927\u4E86\u3002</p><p>\u770B\u5230\u4EA8\u5229\u6C89\u9ED8\u4E0D\u8BED\u674E\u5BCC\u8D35\u62CD\u4E86\u62CD\u4ED6\u7684\u80A9\u8180\uFF1A\u201C\u770B\u6765\u60A8\u4E0D\u518D\u575A\u6301\u8FD9\u4E2A\u4F2A\u5584\u7684\u89C2\u70B9\u4E86\uFF0C\u5F88\u597D\u6211\u4EEC\u53EF\u4EE5\u7ED3\u675F\u8FD9\u573A\u8BA8\u8BBA\u53BB\u5403\u996D\u4E86\u3002\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u60A8\u4E00\u5B9A\u60F3\u5728\u9910\u684C\u4E0A\u70B9\u4E00\u76D8\u72D7\u8089\u5427\uFF1F\u201D\u4EA8\u5229\u7684\u8BED\u6C14\u6709\u70B9\u9178\u9178\u7684\u3002</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u5662\uFF0C\u4E0D\u597D\u610F\u601D\uFF0C\u6211\u4E0D\u559C\u6B22\u5403\u72D7\u8089\u3002\u201D</p><p>\u4EA8\u5229\uFF1A\u201C\u90A3\uFF0C\u90A3\u60A8\u521A\u624D\u548C\u6211\u4E89\u4EC0\u4E48\uFF1F\u201D\u4EA8\u5229\u5F20\u5927\u4E86\u5634\u3002</p><p>\u674E\u5BCC\u8D35\uFF1A\u201C\u6211\u5F88\u6B23\u8D4F\u4F60\u4EEC\u897F\u65B9\u7684\u4E00\u53E5\u8BDD\u2018\u6211\u4E0D\u540C\u610F\u4F60\u7684\u89C2\u70B9\uFF0C\u4E0D\u8FC7\u6211\u4F1A\u7528\u9C9C\u8840\u6765\u634D\u536B\u4F60\u8868\u8FBE\u4F60\u7684\u89C2\u70B9\u7684\u6743\u5229\u2019\uFF0C\u540C\u6837\u6211\u4E0D\u559C\u6B22\u5403\u72D7\u8089\u4F46\u6211\u5C06\u634D\u536B\u522B\u4EBA\u5403\u72D7\u8089\u7684\u6743\u5229\u3002\u201D</p></blockquote>',2),_=[r];function s(c,n,d,i,l,h){return e(),t("div",null,_)}var T=p(o,[["render",s]]);export{f as __pageData,T as default};
